INSERT INTO jatekok(jatek_id, fejleszto_id, kiado_id, nev, megjelenesi_datum, ar, ajanlott_eletkor, kep_id, kep_db, db, leiras) VALUES
(1, 1, 1, 'Counter-Strike', '2000-11-01', 7.19, 0, 1, 5, 357, 'A multiplayer lövöldözős játék, ahol egy csapat terroristák a másik csapat antiterroristái ellen küzd, küldetéseik középkori, modern vagy sci-fi környezetben zajlanak.'),
(2, 1, 1, 'Team Fortress Classic', '1999-04-01', 3.99, 0, 2, 5, 56, 'Egy csapat-alapú, multiplayer lövöldözős játék, ahol különböző karakterekkel rendelkező csapatok versenyeznek egymás ellen különféle térképeken.'),
(3, 1, 1, 'Day of Defeat', '2003-05-01', 3.99, 0, 3, 5, 220, 'Egy második világháborús multiplayer lövöldözős játék, ahol az amerikai és német csapatok küzdenek egymás ellen történelmi helyszíneken.'),
(4, 1, 1, 'Deathmatch Classic', '2001-06-01', 3.99, 0, 4, 4, 282, 'Egy klasszikus deathmatch játék, ahol a játékosoknak egyetlen céljuk van: túlélés és minél több ellenfél legyőzése egy arénában.'),
(5, 2, 1, 'Half-Life: Opposing Force', '1999-11-01', 3.99, 0, 5, 5, 191, 'Egy kiegészítő a Half-Life-hoz, ahol a játékos egy katonát irányít, aki egy katasztrófa sújtotta kísérleti létesítményen küzd a különféle veszélyek ellen.'),
(6, 1, 1, 'Ricochet', '2000-11-01', 3.99, 0, 6, 4, 256, 'Egy multiplayer lövöldözős játék, ahol a játékosok célja, hogy másokat kilökjenek egy háromdimenziós térképen használva a játékra jellemző csillagdobálókat.'),
(7, 1, 1, 'Half-Life', '1998-11-08', 7.19, 0, 7, 5, 100, 'Az első személyes lövöldözős játék, amely egyedülálló történettel és egyedülálló karakterekkel rendelkezik, amikor a játékosnak meg kell szembesülnie az extra dimenziókkal.'),
(8, 1, 1, 'Counter-Strike: Condition Zero', '2004-03-01', 7.19, 0, 8, 5, 480, 'A Counter-Strike sorozat egy változata, amely single-player módot is tartalmaz, így a játékosnak lehetősége van mesterséges intelligencia ellen játszani.'),
(9, 2, 1, 'Half-Life: Blue Shift', '2001-06-01', 3.99, 0, 9, 5, 213, 'Egy további Half-Life kiegészítő, amelyben a játékos a Black Mesa Complex biztonsági őre, Barney Calhoun bőrébe bújik, és megpróbál túlélni a katasztrófát.'),
(10, 1, 1, 'Half-Life 2', '2004-11-16', 7.19, 0, 10, 5, 424, 'A Half-Life folytatása, amely egy új történetet és számos innovatív játékmechanikát hozott be, például a Gravity Gun-t.'),
(11, 1, 1, 'Counter-Strike: Source', '2004-11-01', 7.19, 0, 11, 5, 56, 'A Counter-Strike modernizált változata a Source Engine grafikus motorral, amely továbbfejlesztett grafikát és fizikai motorokat tartalmaz.'),
(12, 1, 1, 'Half-Life: Source', '2004-06-01', 4.99, 0, 12, 4, 63, 'A Half-Life eredeti változata, de a Source Engine felhasználásával, amely javított grafikát és egyéb fejlesztéseket biztosított a játékhoz.'),
(13, 1, 1, 'Day of Defeat: Source', '2010-07-12', 7.19, 0, 13, 5, 428, 'A Day of Defeat modern változata a Source Engine-t használva, amely frissített grafikát és valósághűbb harcrendszert biztosít.'),
(14, 1, 1, 'Half-Life 2: Deathmatch', '2004-11-01', 3.99, 0, 14, 5, 299, 'Egy multiplayer deathmatch játék a Half-Life 2 világában, ahol a játékosok egymás ellen harcolnak különböző pályákon.'),
(15, 1, 1, 'Half-Life 2: Lost Coast', '2005-10-27', 4.99, 0, 15, 5, 203, 'Egy rövid, de látványos kiegészítő a Half-Life 2-höz, ahol a játékosnak egy elhagyatott partszakaszon kell küzdenie a különböző veszélyek ellen.'),
(16, 1, 1, 'Half-Life Deathmatch: Source', '2006-05-01', 4.99, 0, 16, 5, 245, 'A Half-Life multiplayer részlegének Source Engine-s változata, ahol a játékosok egymás ellen harcolnak a játék egyedi térképein.'),
(17, 1, 1, 'Half-Life 2: Episode One', '2006-06-01', 5.79, 0, 17, 5, 136, 'A Half-Life 2 első epizódja, amely továbbviszi a történetet és új kihívások elé állítja a játékosokat a város pusztulása közepette.'),
(18, 1, 1, 'Portal', '2007-10-10', 7.19, 0, 18, 5, 213, 'Egy egyedülálló first-person puzzle-platformer, ahol a játékosnak portálokat kell létrehoznia és kihasználnia azokat a különböző feladványok megoldásához.'),
(19, 1, 1, 'Half-Life 2: Episode Two', '2007-10-10', 5.79, 0, 19, 5, 336, 'A Half-Life 2 második epizódja, amelyben a történet folytatódik, és a játékosnak újabb kihívásokkal kell szembenéznie a földön és azon kívül.'),
(20, 1, 1, 'Team Fortress 2', '2007-10-10', 4.99, 0, 20, 5, 175, 'Egy humoros és stílusos multiplayer lövöldözős játék, ahol a játékosok különböző karaktereket irányíthatnak, mindegyik más és más képességekkel rendelkezik.'),
(21, 1, 1, 'Left 4 Dead', '2008-11-17', 7.19, 0, 21, 5, 178, 'Egy kooperatív túlélőhorror játék, ahol a játékosok egy csoport tagjaként próbálnak túlélni a zombi hordákkal teli környezetben.'),
(22, 1, 1, 'Left 4 Dead 2', '2009-11-19', 7.19, 0, 22, 5, 273, 'A Left 4 Dead folytatása, amely új karaktereket, térképeket és zombi típusokat vezet be, továbbfejlesztve a kooperatív túlélőhorror élményt.'),
(23, 1, 1, 'Dota 2', '2013-07-09', 4.99, 0, 23, 5, 475, 'Egy ingyenesen játszható multiplayer online battle arena (MOBA) játék, ahol a játékosok különböző hősökkel versenyeznek egymás ellen egy arénában.'),
(24, 1, 1, 'Portal 2', '2011-04-18', 7.19, 0, 24, 5, 21, 'A Portal folytatása, amely továbbfejleszti az eredeti játék mechanikáját és egy kooperatív módot is tartalmaz, ahol két játékosnak kell együttműködnie a feladványok megoldásához.'),
(25, 1, 1, 'Alien Swarm', '2010-07-19', 4.99, 0, 25, 5, 247, 'Egy ingyenes, kooperatív többjátékos lövöldözős játék, ahol a játékosoknak egy marines egységet kell irányítaniuk, és meg kell küzdeniük az űrhajóban található alien lényekkel.'),
(26, 3, 1, 'Counter-Strike: Global Offensive', '2012-08-21', 4.99, 0, 26, 5, 437, 'A Counter-Strike sorozat legújabb része, amely továbbfejlesztett grafikát, új pályákat és játékmódokat kínál a sorozat rajongóinak.'),
(27, 4, 2, 'Rag Doll Kung Fu', '2005-10-12', 5.99, 0, 27, 5, 468, 'Egy humoros és stílusos verekedős játék, ahol a játékosok irányíthatják egyedi, sallangos karaktereiket és vívhatnak egymással különböző pályákon.'),
(28, 5, 3, 'Red Orchestra: Ostfront 41-45', '2006-03-14', 3.99, 0, 28, 5, 314, 'Egy második világháborús multiplayer lövöldözős játék, ahol a játékosok az Orosz Fronton harcolnak az északi és keleti területeken.'),
(29, 5, 3, 'Killing Floor', '2009-05-14', 14.99, 0, 29, 5, 402, 'Egy kooperatív túlélőhorror játék, ahol a játékosoknak meg kell védeniük magukat és egymást a zombik és mutáns szörnyek ellen.'),
(30, 6, 4, 'SiN Episodes: Emergence', '2006-05-10', 7.19, 0, 30, 5, 83, 'Egy első személyes lövöldözős játék, ahol a játékosnak egy anti-terrorista ügynök bőrébe kell bújnia, és meg kell állítania egy globális összeesküvést.'),
(31, 7, 5, 'Darwinia', '2005-07-14', 7.19, 0, 31, 5, 255, 'Egy egyedi stratégiai játék, ahol a játékosok egy virtuális világban irányíthatják a "Darwinian" lényeket és küzdenek más mesterséges intelligencia ellen.'),
(32, 7, 5, 'Uplink', '2006-08-23', 6.99, 0, 32, 4, 77, 'Egy szimulátor, ahol a játékos egy hackerként kezd, és különböző számítógépes rendszerekbe hatolva próbál információkat lopni vagy manipulálni.'),
(33, 7, 5, 'DEFCON', '2006-09-29', 7.19, 0, 33, 5, 57, 'Egy stratégiai játék, amely a hidegháborús idők atomháborúját szimulálja, ahol a játékosnak meg kell terveznie és végrehajtania kell az országa védelmét és támadását.'),
(34, 7, 5, 'Multiwinia', '2008-09-19', 7.19, 0, 34, 5, 364, 'Egy multiplayer stratégiai játék, ahol a játékosok kis lények vezetésével versenyeznek egymás ellen egy virtuális térképen.'),
(35, 8, 6, 'Dangerous Waters', '2006-02-07', 22.99, 0, 35, 5, 448, 'Egy szimulátor, ahol a játékosok tengeralattjárót vagy más hadihajót irányítanak, és részt vesznek különböző haditengerészeti küldetésekben.'),
(36, 9, 6, 'Space Empires IV Deluxe', '2006-02-07', 6.99, 0, 36, 5, 260, 'Egy űrstratégiai játék, ahol a játékosoknak egy galaktikus birodalmat kell irányítaniuk, kutatniuk, diplomáciai kapcsolatokat kiépíteniük és hadat viselniük mások ellen.'),
(37, 10, 6, 'Disciples II: Rise of the Elves', '2006-07-06', 4.99, 0, 37, 5, 380, 'Egy fantasy szerepjáték és stratégiai keverék, ahol a játékosoknak egy sötét fantasy világban kell csapatokat építeniük és vezetniük a győzelem érdekében.'),
(38, 10, 6, 'Disciples II: Gallean''s Return', '2006-07-06', 4.99, 0, 38, 4, 51, 'Egy Disciples II kiegészítő, amely további történetet, karaktereket és kihívásokat kínál a játékosoknak az eredeti játékhoz.'),
(39, 10, 6, 'Iron Warriors: T - 72 Tank Command', '2006-07-26', 3.99, 0, 39, 5, 26, 'Egy tank szimulátor, ahol a játékosnak egy T-72 típusú tankot kell irányítania és részt kell vennie különböző tankcsatákban és küldetésekben.'),
(40, 9, 6, 'Space Empires V', '2006-10-16', 10.99, 0, 40, 5, 363, 'Egy űrstratégiai játék, ahol a játékosnak egy űrkorszakot kell irányítania, területeket felfedezni, birodalmat építeni, diplomáciai kapcsolatokat kialakítani és hadat viselni.'),
(41, 11, 7, 'Arx Fatalis', '2007-04-03', 2.99, 0, 41, 5, 232, 'Egy első személyes szerepjáték, ahol a játékosnak egy földalatti világban kell túlélésért küzdenie, rejtélyeket megoldania és szörnyekkel kell megküzdenie.'),
(42, 1, 1, 'Source Filmmaker', '2012-07-10', 4.99, 0, 42, 5, 126, 'Egy animációs és filmmegalkotó eszköz, amely a Source Engine-t használja, lehetővé téve a felhasználók számára, hogy saját játékfilmjeiket és animációikat készítsék el.'),
(43, 12, 8, 'Earth 2160', '2006-04-01', 3.99, 0, 43, 5, 256, 'Egy sci-fi valós idejű stratégiai játék, ahol a játékosoknak egy ősi faj képviselőjeként kell harcolniuk mások ellen, miközben forrásokat gyűjtenek és technológiát fejlesztenek.'),
(44, 12, 9, 'Two Worlds Epic Edition', '2009-04-30', 6.99, 0, 44, 5, 147, 'Egy nyílt világú szerepjáték, ahol a játékosok egy fantasztikus világban kalandozhatnak, küldetéseket teljesíthetnek, szörnyekkel harcolhatnak és kincseket gyűjthetnek.'),
